// const redux = require('redux'); //import reduex
import {createSlice, configureStore} from '@reduxjs/toolkit'

const initialState = {counter: 0, showCounter: true}

const counterSlice = createSlice({
    name:"counter",
    initialState,
    reducers: {
        increment(state){
            state.counter++
        },
        discrement(state){
            state.counter--
        },
        increse(state, action){
            state.counter=state.counter+action.payload
        },
        toggle(state){
            state.showCounter=!state.showCounter
        }
    }
})

const counterReducer = (state = initialState, action) => {     //store
    if(action.type === 'increment'){
        return {
            counter: state.counter+1,
            showCounter: state.showCounter
        }
    }

    if(action.type === 'increse'){
        return {
            counter: state.counter+action.amount,
            showCounter: state.showCounter
        }
    }
    
    if(action.type === 'discrement'){
        return {
            counter: state.counter-1,
            showCounter: state.showCounter
        }
    }

    if(action.type === 'toggle'){
        return {
            showCounter: !state.showCounter,
            counter: state.counter
        }
    }
}

const store = configureStore({
    reducer: counterSlice.reducer
}); //create store

console.log(store.getState());

const counterSubscriber = () => {               
    const latestState = store.getState();
    console.log(latestState)
}

store.subscribe(counterSubscriber)      //method call when store is changing

store.dispatch({type: 'increment'});    //change store

export const counterActions = counterSlice.actions
export default store
