// const redux = require('redux'); //import redux
import {createSlice, configureStore} from '@reduxjs/toolkit'

const initialCounterState = {counter: 0, showCounter: true}

const counterSlice = createSlice({ // create store data
    name:"counter",
    initialState: initialCounterState,
    reducers: {
        increment(state){
            state.counter++
        },
        discrement(state){
            state.counter--
        },
        increse(state, action){
            state.counter=state.counter+action.payload
        },
        toggle(state){
            state.showCounter=!state.showCounter
        }
    }
})

const initialAuthState = {isLogin: false};

const authSlice = createSlice({
    name: 'auth',
    initialState: initialAuthState,
    reducers: {
        logIn(state){},
        logOut(state){}
    }
})

const store = configureStore({ //create store
    reducer:{counter: counterSlice.reducer, auth: authSlice.reducer}
}); 


const counterSubscriber = () => {               
    const latestState = store.getState(); // get store data
    console.log(latestState)
}

store.subscribe(counterSubscriber)      //method call when store is changing

export const counterActions = counterSlice.actions; // for using store actions in another 
export const authActions = authSlice.actions; // for using store actions in another files

export default store
