import {createSlice} from '@reduxjs/toolkit'


const initialAuthState = {isLogin: false};

const authSlice = createSlice({
    name: 'auth',
    initialState: initialAuthState,
    reducers: {
        logIn(state){
            state.isLogin=true
        },
        logOut(state){
            state.isLogin=false
        }
    }})


export default authSlice.reducer
